import React from 'react';
import './ImgLinkForm.css';
import Particles from 'react-particles-js';

const particlesOptions = {
  particles: {
    number: {
      value: 10
    },
    color: {
      value: '#111'
    },
    stroke: {
      width: 150
    },
    line_linked: {
      opacity: 0.8,
      color: '#FF69B4',
      width: 0.6,
      enable_auto: false
    },
    interactivity: {
      onhover: {
        enable: true,
        mode: 'repulse'
      }
    }
  }
}

const ImgLinkForm = () => {
  return (
    <div>
      <p className='f3 center'>
        {'Load a photo and watch the AI detect the faces'}
      </p>
      <div className='form' style={{marginLeft: 'auto', marginRight: 'auto'}}>
        <div className='center pa4 br3 shadow-5'>
          <input className='box f4 pa2 w-70' type='text' style={{marginLeft: 'auto', marginRight: 'auto'}}/>
          <button className='w-30 link ph3 pv2 dib bg-dark'>
            <Particles
              className='particles'
              params={particlesOptions}
            />
            Detect Face
          </button>
        </div>
      </div>
    </div>

  )
}

export default ImgLinkForm;
